use dioxus::prelude::*;

use crate::components::buttons::ActionButton;

const ZBAR_WASM: Asset = asset!("/assets/zbar.wasm");

#[component]
fn BarcodeInput(on_set: Callback<String>) -> Element {
    use_future(move || async move {
        let js_value = serde_json::json!(&ZBAR_WASM.resolve()).to_string();

        let script = [
            r#"
            window['loadBarcodeDetectorPolyfill']({
                locateFile: (file) => "#,
            js_value.as_str(),
            r#"
            });

            // Define video as the video element. You can pass the entire element to the barcode detector!
            const video = document.querySelector('video');

            // Get a stream for the rear camera, else the front (or side?) camera, and show it in the video element.
            video.srcObject = await navigator.mediaDevices.getUserMedia({ video: { facingMode: 'environment' } });

            // Create a BarcodeDetector for simple retail operations.
            const barcodeDetector = new BarcodeDetector({
                formats: ["ean_13", "ean_8", "upc_a", "upc_e"],
            });

            let rawValue = null;

            while(true) {
                try {
                    const video_refresh = document.querySelector('video');
                    if (!video_refresh) {
                        video.srcObject.getTracks().forEach(track => track.stop());
                        break;
                    }

                    // Try to detect barcodes in the current video frame.
                    let barcodes = await barcodeDetector.detect(video);

                    // Continue loop if no barcode was found.
                    if (barcodes.length == 1)
                    {
                        let barcode = barcodes[0];
                        if (barcode.rawValue != rawValue) {
                            // We expect a single barcode.
                            // It's possible to compare X/Y coordinates to get the center-most one.
                            // One can also do "preferred symbology" logic here.
                            var snd = new Audio("data:audio/wav;base64,");
                            snd.play();

                            dioxus.send(barcode.rawValue);
                        }

                        rawValue = barcode.rawValue;
                        await new Promise(r => setTimeout(r, 1000));
                    } else {
                        rawValue = null;
                        await new Promise(r => setTimeout(r, 50));
                    }

                }
                catch(err) {
                    console.log("failed", err);
                    //Wait till video is ready
                    //barcodeDetector.detect(video) might fail the first time
                    await new Promise(r => setTimeout(r, 200));
                }
            }
        "#,
        ].join("");

        let mut eval = document::eval(&script);
        while let Ok(value) = eval.recv::<String>().await {
            on_set(value);
        }
    });

    rsx! {
        video { autoplay: true, playsinline: true }
    }
}

#[component]
pub fn Barcode(barcode: Signal<String>) -> Element {
    let mut show = use_signal(|| false);

    rsx! {
        if show() {
            h1 { "Please scan barcode" }
            BarcodeInput {
                on_set: move |value| {
                    barcode.set(value);
                    show.set(false);
                },
            }
            ActionButton { on_click: move |_| show.set(false), "cancel" }
        } else {
            ActionButton { on_click: move |_| show.set(true), "scan" }
        }
    }
}
